mapper.select:
select t2.BIZ_INF_NO, t5.USER_NAME,t2.BIZ_TYPE ,getDetailName(t3.AUDITE_STATE,'CD_AUDITE_STATE') ORDER_STATUS
,t1.ID  TASKID,
t1.ACTIVITY_NAME CUR_ACT_NAME,
t1.ACTIVITY_NAME ACTIVITYNAME,
t1.PRO_INSTANCE_ID CUR_EXE_ID,
t2.BIZ_TAB_NAME BIZ_TAB_NAME,
t2.ID BIZ_TAB_ID,
t6.USER_NAME CUR_OWNER,
t3.PRODUCT_TYPE ,t3.ENTRY_ORG_ID,t3.ENTRY_ORG_Name,t3.CUSTOMER_MANAGER,
DATE_FORMAT(t3.CREATION_TIME,'%Y-%m-%d %H:%i:%s') ENTRY_DATE,DATE_FORMAT(t2.CREATE_TIME,'%Y-%m-%d %H:%i:%s') END_DATE,t2.BIZ_INF_ID ,'正常'PROINSTANCESTATE
from jbpm4_task t1  INNER JOIN jbpm4_biz_tab t2 ON t1.PRO_INSTANCE_ID =t2.PRO_INSTANCE_ID
LEFT JOIN CAR_LOAN_INFO t3 ON t3.ORDER_NUMBER=t2.BIZ_INF_NO
LEFT JOIN CAR_LOAN_USER  t5 On  t3.ORDER_NUMBER=t5.ORDER_NUMBER
LEFT JOIN sys_user t6 ON  t6.ID=t1.ASSIGNEE
where 1=1
AND t2.IS_OVER=0
AND ( t1.ASSIGNEE IN (select target_id from sys_role_user where role_id IN(
select ID from sys_role where role_code in(
select MAPPING_ROLE_CODE from sys_role_mapping where role_code in(
SELECT role_code from sys_role where id in(
select role_id from sys_role_user where target_id=#{dto.aSSIGNEE})))) and sys_role_user.validate_state=1 UNION  SELECT #{dto.aSSIGNEE}))

    AND t2.BIZ_INF_NO=#{dto.bIZINFNO}


    AND t5.USER_NAME=#{dto.uSERNAME}


    AND  t6.USER_NAME=#{dto.cUROWNER}


    AND t3.PRODUCT_SERIES=#{dto.pRODUCTTYPE}


    AND t2.BIZ_TYPE=#{dto.bIZTYPE}


    AND t3.SECOND_LEVEL_ID=#{dto.SECONDLEVELID}


    AND t3.ENTRY_ORG_ID=#{dto.eNTRYORGID}


    AND t1.ACTIVITY_NAME=#{dto.aCTIVITYNAME}


    AND t3.CREATION_TIME>=#{dto.crMsgdateStr}


    AND t3.CREATION_TIME  <=  #{dto.crMsgdateEnd}



    AND t2.CREATE_TIME >=#{dto.eNTRYDATEStr}


    AND t2.CREATE_TIME  <=  #{dto.eNTRYDATEEnd}


order by t1.ID desc

select t2.BIZ_INF_NO, t5.USER_NAME,t2.BIZ_TYPE ,getDetailName(t3.AUDITE_STATE,'CD_AUDITE_STATE') ORDER_STATUS
,t1.ID  TASKID,
t1.ACTIVITY_NAME CUR_ACT_NAME,
t1.ACTIVITY_NAME ACTIVITYNAME,
t1.PRO_INSTANCE_ID CUR_EXE_ID,
t2.BIZ_TAB_NAME BIZ_TAB_NAME,
t2.ID BIZ_TAB_ID,
t6.USER_NAME CUR_OWNER,
t3.PRODUCT_TYPE ,t3.ENTRY_ORG_ID,t3.ENTRY_ORG_Name,t3.CUSTOMER_MANAGER,
DATE_FORMAT(t3.CREATION_TIME,'%Y-%m-%d %H:%i:%s') ENTRY_DATE,DATE_FORMAT(t1.END_TIME,'%Y-%m-%d %H:%i:%s') END_DATE,t2.BIZ_INF_ID ,'正常'PROINSTANCESTATE
from jbpm4_hist_task t1 INNER JOIN jbpm4_biz_tab t2 ON t1.PRO_INSTANCE_ID =t2.PRO_INSTANCE_ID
LEFT JOIN CAR_LOAN_INFO t3 ON t3.ORDER_NUMBER=t2.BIZ_INF_NO
LEFT JOIN CAR_LOAN_USER  t5 On  t3.ORDER_NUMBER=t5.ORDER_NUMBER
LEFT JOIN sys_user t6 ON  t6.ID=t1.ASSIGNEE
where 1=1
AND t1.END_TIME IS NOT NULL
AND t2.IS_OVER=0
AND ( t1.ASSIGNEE IN (select target_id from sys_role_user where role_id IN(
select ID from sys_role where role_code in(
select MAPPING_ROLE_CODE from sys_role_mapping where role_code in(
SELECT role_code from sys_role where id in(
select role_id from sys_role_user where target_id=#{dto.aSSIGNEE})))) and sys_role_user.validate_state=1 UNION  SELECT #{dto.aSSIGNEE}))

    AND t2.BIZ_INF_NO=#{dto.bIZINFNO}


    AND t5.USER_NAME=#{dto.uSERNAME}


    AND t6.USER_NAME=#{dto.cUROWNER}


    AND t3.PRODUCT_SERIES=#{dto.pRODUCTTYPE}


    AND t2.BIZ_TYPE=#{dto.bIZTYPE}


    AND t3.SECOND_LEVEL_ID=#{dto.SECONDLEVELID}


    AND t3.ENTRY_ORG_ID=#{dto.eNTRYORGID}


    AND t1.ACTIVITY_NAME=#{dto.aCTIVITYNAME}


    AND t1.END_TIME>=#{dto.crMsgdateStr}


    AND t1.END_TIME  <=  #{dto.crMsgdateEnd}



    AND t3.CREATION_TIME >=#{dto.eNTRYDATEStr}


    AND t3.CREATION_TIME  <=  #{dto.eNTRYDATEEnd}

order by t1.ID desc

select  t2.BIZ_INF_NO, t5.USER_NAME,t5.PHONE_NUMBER,t2.BIZ_TYPE ,t4.PRODUCT_NAME,getDetailName(t3.AUDITE_STATE,'CD_AUDITE_STATE') ORDER_STATUS
,'' TASKID,
t2.PRO_INSTANCE_ID CUR_EXE_ID,
t2.BIZ_TAB_NAME BIZ_TAB_NAME,
t2.ID BIZ_TAB_ID,
t3.PRODUCT_TYPE ,t3.ENTRY_ORG_ID,t3.ENTRY_ORG_Name,t3.CUSTOMER_MANAGER,
DATE_FORMAT(t3.ONE_START,'%Y-%m-%d %H:%i:%s') ENTRY_DATE,
DATE_FORMAT(t3.ONE_END,'%Y-%m-%d %H:%i:%s') END_DATE,
DATE_FORMAT(t3.CREATION_TIME,'%Y-%m-%d %H:%i:%s') crMsgdate,
t2.BIZ_INF_ID,'正常'PROINSTANCESTATE
from jbpm4_biz_tab t2
LEFT JOIN CAR_LOAN_INFO t3 ON t3.ORDER_NUMBER=t2.BIZ_INF_NO
LEFT JOIN lb_t_product_conf t4 ON t4.PRODUCT_CODE=t3.PRODUCT_TYPE
LEFT JOIN CAR_LOAN_USER  t5 On  t3.ORDER_NUMBER=t5.ORDER_NUMBER
where 1=1
AND t2.IS_OVER=1
AND ( t2.BIZ_INF_NO IN (select t10.BIZ_INF_NO from jbpm4_hist_task t9 INNER JOIN jbpm4_biz_tab t10 ON t9.PRO_INSTANCE_ID =t10.PRO_INSTANCE_ID LEFT JOIN sys_user t6 ON  t6.ID IN (select target_id from sys_role_user where role_id IN(
select ID from sys_role where role_code in(
select MAPPING_ROLE_CODE from sys_role_mapping where role_code in(
SELECT role_code from sys_role where id in(
select role_id from sys_role_user where target_id=#{dto.aSSIGNEE})))) and sys_role_user.validate_state=1 UNION  SELECT #{dto.aSSIGNEE})))

    AND t2.BIZ_INF_NO=#{dto.bIZINFNO}


    AND t5.USER_NAME=#{dto.uSERNAME}


    AND  t2.BIZ_INF_NO in(select t10.BIZ_INF_NO from jbpm4_hist_task t9
    INNER JOIN jbpm4_biz_tab t10 ON t9.PRO_INSTANCE_ID =t10.PRO_INSTANCE_ID
    LEFT JOIN sys_user t6 ON  t6.ID=t9.ASSIGNEE where t6.USER_NAME=#{dto.cUROWNER}  and t10.IS_OVER=1)


    AND t3.PRODUCT_SERIES=#{dto.pRODUCTTYPE}


    AND t3.ONE_END>=#{dto.eNDDATEStr}


    AND t3.ONE_END  <=  #{dto.eNDDATEEnd}



    AND t3.ENTRY_DATE >=#{dto.eNTRYDATEStr}


    AND t3.ENTRY_DATE  <=  #{dto.eNTRYDATEEnd}


    AND t3.CREATION_TIME>=#{dto.crMsgdateStr}


    AND t3.CREATION_TIME  <=  #{dto.crMsgdateEnd}


    AND t3.SECOND_LEVEL_ID=#{dto.SECONDLEVELID}


    AND t3.ENTRY_ORG_ID=#{dto.eNTRYORGID}

order by t2.ID desc
