mapper.select:
    	 		select 
						t1.id                             ,
						t1.order_number                   ,
						t1.survey_contacts_id             ,
						t1.check_time                     ,
						t1.check_status                   ,
						t1.examine_stage                  ,
						t1.owner_id                       ,
						t1.org_id                         ,
						t1.create_time                    ,
						t1.modify_time                    ,
						t1.create_by                      ,
						t1.remark                         

				from car_loan_cal_phone_log 	t1
				where 1=1

						and t1.id =#{dto.id}


						and t1.ORDER_NUMBER =#{dto.orderNumber}


						and t1.SURVEY_CONTACTS_ID =#{dto.surveyContactsId}


						and t1.CHECK_TIME =#{dto.checkTime}


						and t1.CHECK_STATUS =#{dto.checkStatus}


						and t1.EXAMINE_STAGE =#{dto.examineStage}


						and t1.OWNER_ID =#{dto.ownerId}


						and t1.ORG_ID =#{dto.orgId}


						and t1.CREATE_TIME =#{dto.createTime}


						and t1.MODIFY_TIME =#{dto.modifyTime}


						and t1.CREATE_BY =#{dto.createBy}


						and t1.REMARK =#{dto.remark}


				order by t1.id desc

    	     select 
						t1.id                             ,
						t1.order_number                   ,
						t1.survey_contacts_id             ,
						t1.check_time                     ,
						t1.check_status                   ,
						t1.examine_stage                  ,
						t1.owner_id                       ,
						t1.org_id                         ,
						t1.create_time                    ,
						t1.modify_time                    ,
						t1.create_by                      ,
						t1.remark                         
				from car_loan_cal_phone_log 		t1
				where 1=1

						and t1.id = #{dto.id}


						and t1.ORDER_NUMBER = #{dto.orderNumber}


						and t1.SURVEY_CONTACTS_ID = #{dto.surveyContactsId}


						and t1.CHECK_TIME = #{dto.checkTime}


						and t1.CHECK_STATUS = #{dto.checkStatus}


						and t1.EXAMINE_STAGE = #{dto.examineStage}


						and t1.OWNER_ID = #{dto.ownerId}


						and t1.ORG_ID = #{dto.orgId}


						and t1.CREATE_TIME = #{dto.createTime}


						and t1.MODIFY_TIME = #{dto.modifyTime}


						and t1.CREATE_BY = #{dto.createBy}


						and t1.REMARK = #{dto.remark}

mapper.insert:
insert into car_loan_cal_phone_log
