mapper.select:
select t1.id , t1.order_number , t1.operation_action , t1.req_json , t1.err_json , t1.creation_time , t1.update_time
from car_loan_log t1 where 1=1
and t1.id =#{dto.id}
and t1.ORDER_NUMBER =#{dto.orderNumber}
and t1.OPERATION_ACTION =#{dto.operationAction}
order by t1.id desc

    	     select 
						t1.id                             ,
						t1.order_number                   ,
						getDetailName(t1.operation_action,'CAR_LOG_TYPE')operation_action,
						t1.req_json                       ,
						t1.err_json                       ,
						t1.creation_time                  ,
						t1.update_time                    
				from car_loan_log 		t1
				where 1=1

						and t1.id = #{dto.id}


						and t1.ORDER_NUMBER = #{dto.orderNumber}


						and t1.OPERATION_ACTION = #{dto.operationAction}


						and t1.REQ_JSON = #{dto.reqJson}


						and t1.ERR_JSON = #{dto.errJson}


					and DATE_FORMAT(t1.CREATION_TIME,'%Y-%m-%d') >=#{dto.creationTimeSta}



						and DATE_FORMAT(t1.CREATION_TIME,'%Y-%m-%d') <=#{dto.creationTimeEnd}



						and t1.ERR_JSON =""




					and t1.ERR_JSON !=""


				order by t1.CREATION_TIME desc

mapper.insert:
insert into car_loan_log
(   		
			id                             ,
			order_number                   ,
			operation_action               ,
			req_json                       ,
			err_json                       ,
			creation_time                  ,
			update_time                    
)
values(
			#{dto.id,jdbcType=DECIMAL}
			,
			#{dto.orderNumber,jdbcType=VARCHAR}
			,
			#{dto.operationAction,jdbcType=VARCHAR}
			,
			#{dto.reqJson,jdbcType=CLOB}
			,
			#{dto.errJson,jdbcType=CLOB}
			,
			now()
			,
			#{dto.updateTime,jdbcType=TIMESTAMP}

)
