mapper.select:
    	 		select 
						t1.id                             ,
						t1.dict_code                      ,
						t1.dict_name                      ,
						t1.dict_type                      ,
						t1.validate_state                 ,
						t1.version                        

				from sys_dict 	t1
				where 1=1

						and t1.ID =#{dto.id}


						and t1.DICT_CODE =#{dto.dictCode}


						and t1.DICT_NAME =#{dto.dictName}


						and t1.DICT_TYPE =#{dto.dictType}


						and t1.VALIDATE_STATE =#{dto.validateState}


						and t1.VERSION =#{dto.version}


				order by t1.id desc

    	     select 
						t1.id                             ,
						t1.dict_code                      ,
						t1.dict_name                      ,
						t1.dict_type                      ,
						t1.validate_state                 ,
						t1.version                        
				from sys_dict 		t1
				where 1=1

						and t1.ID = #{dto.id}


						and t1.DICT_CODE = #{dto.dictCode}


						and t1.DICT_NAME = #{dto.dictName}


						and t1.DICT_TYPE = #{dto.dictType}


						and t1.VALIDATE_STATE = #{dto.validateState}


						and t1.VERSION = #{dto.version}

mapper.update:
     update sys_dict t1
           set 

					 t1.dict_code                      =#{dto.dictCode,jdbcType=VARCHAR},


					 t1.dict_name                      =#{dto.dictName,jdbcType=VARCHAR}


     where t1.dict_code = #{dto.dictCode}

mapper.select:
select
t1.id                             ,
t1.dict_code                      ,
t1.dict_name                      ,
t1.dict_type                      ,
t1.validate_state                 ,
t1.version                        
		from sys_dict 		t1
		where 1=1

and t1.ID = #{dto.id}


and t1.DICT_CODE = #{dto.dictCode}


and t1.DICT_NAME = #{dto.dictName}


and t1.DICT_TYPE = #{dto.dictType}


and t1.VALIDATE_STATE = #{dto.validateState}


and t1.VERSION = #{dto.version}
