mapper.select:
    	 		select 
						t1.id                             ,
						t1.dict_id                        ,
						t1.dict_detail_name               ,
						t1.dict_detail_value              ,
						t1.order_by                       ,
						t1.validate_state                 ,
						t1.version                        ,
						t1.creation_time                  

				from sys_dict_detail 	t1
				where 1=1

						and t1.ID =#{dto.id}


						and t1.DICT_ID =#{dto.dictId}


						and t1.DICT_DETAIL_NAME =#{dto.dictDetailName}


						and t1.DICT_DETAIL_VALUE =#{dto.dictDetailValue}


						and t1.ORDER_BY =#{dto.orderBy}


						and t1.VALIDATE_STATE =#{dto.validateState}


						and t1.VERSION =#{dto.version}


						and t1.CREATION_TIME =#{dto.creationTime}


				order by t1.id desc

    	     select 
						t1.id                             ,
						t1.dict_id                        ,
						t1.dict_detail_name               ,
						t1.dict_detail_value              ,
						t1.order_by                       ,
						t1.validate_state                 ,
						t1.version                        ,
						t1.creation_time                  
				from sys_dict_detail 		t1
				where 1=1

						and t1.ID = #{dto.id}


						and t1.DICT_ID = #{dto.dictId}


						and t1.DICT_DETAIL_NAME = #{dto.dictDetailName}


						and t1.DICT_DETAIL_VALUE = #{dto.dictDetailValue}


						and t1.ORDER_BY = #{dto.orderBy}


						and t1.VALIDATE_STATE = #{dto.validateState}


						and t1.VERSION = #{dto.version}


						and t1.CREATION_TIME = #{dto.creationTime}

mapper.insert:
insert into sys_dict_detail
(   		
			dict_id                        ,
			dict_detail_name               ,
			dict_detail_value              ,
			order_by                       ,
			validate_state                 ,
			version                        ,
			creation_time                  
)
values(
			#{dto.dictId,jdbcType=DECIMAL}
			,
			#{dto.dictDetailName,jdbcType=VARCHAR}
			,
			#{dto.dictDetailValue,jdbcType=VARCHAR}
			,
			#{dto.orderBy,jdbcType=VARCHAR}
			,
			'1'
			,
			#{dto.version,jdbcType=DECIMAL}
			,
			#{dto.creationTime,jdbcType=TIMESTAMP}

)

mapper.select:
select
t1.id                             ,
t1.dict_id                        ,
t1.dict_detail_name               ,
t1.dict_detail_value              ,
t1.order_by                       ,
t1.validate_state                 ,
t1.version                        ,
t1.creation_time                  
		from sys_dict_detail 		t1
		where 1=1

and t1.ID = #{dto.id}


and t1.DICT_ID = #{dto.dictId}


and t1.DICT_DETAIL_NAME = #{dto.dictDetailName}


and t1.DICT_DETAIL_VALUE = #{dto.dictDetailValue}


and t1.ORDER_BY = #{dto.orderBy}


and t1.VALIDATE_STATE = #{dto.validateState}


and t1.VERSION = #{dto.version}


and t1.CREATION_TIME = #{dto.creationTime}
