mapper.select:
select
t1.id ,
t1.menu_code ,
t1.menu_name ,
t1.menu_icon ,
t1.menu_url ,
t1.parent_id ,
t1.order_by ,
t1.validate_state ,
t1.app_id ,
t1.version ,
t1.resource_id

from sys_menu t1
where 1=1

    and t1.ID =#{dto.id}


    and t1.MENU_CODE =#{dto.menuCode}


    and t1.MENU_NAME =#{dto.menuName}


    and t1.MENU_ICON =#{dto.menuIcon}


    and t1.MENU_URL =#{dto.menuUrl}


    and t1.PARENT_ID =#{dto.parentId}


    and t1.ORDER_BY =#{dto.orderBy}


    and t1.VALIDATE_STATE =#{dto.validateState}


    and t1.APP_ID =#{dto.appId}


    and t1.VERSION =#{dto.version}


    and t1.RESOURCE_ID =#{dto.resourceId}


order by t1.id desc

select
t1.id ,
t1.menu_code ,
t1.menu_name ,
t1.menu_icon ,
t1.menu_url ,
t1.parent_id ,
t1.order_by ,
t1.validate_state ,
t1.app_id ,
t1.version ,
t1.resource_id
from sys_menu t1
where 1=1

    and t1.ID = #{dto.id}


    and t1.MENU_CODE = #{dto.menuCode}


    and t1.MENU_NAME = #{dto.menuName}


    and t1.MENU_ICON = #{dto.menuIcon}


    and t1.MENU_URL = #{dto.menuUrl}


    and t1.PARENT_ID = #{dto.parentId}


    and t1.ORDER_BY = #{dto.orderBy}


    and t1.VALIDATE_STATE = #{dto.validateState}


    and t1.APP_ID = #{dto.appId}


    and t1.VERSION = #{dto.version}


    and t1.RESOURCE_ID = #{dto.resourceId}

mapper.insert:
insert into sys_menu
(   		
			menu_code                      ,
			menu_name                      ,
			menu_icon                      ,
			menu_url                       ,
			parent_id                      ,
			order_by                       ,
			validate_state                 ,
			app_id                         ,
			version                        ,
			resource_id                    
)
values(
			#{dto.menuCode,jdbcType=VARCHAR}
			,
			#{dto.menuName,jdbcType=VARCHAR}
			,
			#{dto.menuIcon,jdbcType=VARCHAR}
			,
			#{dto.menuUrl,jdbcType=VARCHAR}
			,
			#{dto.parentId,jdbcType=VARCHAR}
			,
			#{dto.orderBy,jdbcType=VARCHAR}
			,
			'1'
			,
			#{dto.appId,jdbcType=DECIMAL}
			,
			#{dto.version,jdbcType=DECIMAL}
			,
			#{dto.resourceId,jdbcType=DECIMAL}

)

mapper.select:
select
t1.id ,
t1.menu_code ,
t1.menu_name ,
t1.menu_icon ,
t1.menu_url ,
t1.parent_id ,
t1.order_by ,
t1.validate_state ,
t1.app_id ,
t1.version ,
t1.resource_id
from sys_menu t1
where 1=1

    and t1.ID = #{dto.id}


    and t1.MENU_CODE = #{dto.menuCode}


    and t1.MENU_NAME = #{dto.menuName}


    and t1.MENU_ICON = #{dto.menuIcon}


    and t1.MENU_URL = #{dto.menuUrl}


    and t1.PARENT_ID = #{dto.parentId}


    and t1.ORDER_BY = #{dto.orderBy}


    and t1.VALIDATE_STATE = #{dto.validateState}


    and t1.APP_ID = #{dto.appId}


    and t1.VERSION = #{dto.version}


    and t1.RESOURCE_ID = #{dto.resourceId}

SELECT DISTINCT e.*,a.resoure_url,a.resoure_type,a.permission FROM sys_resource a LEFT JOIN sys_acl b ON b.resoure_id = a.id LEFT JOIN sys_role c ON b.role_id = c.id left join sys_role_user d on c.id=d.ROLE_ID left join sys_menu e on e.RESOURCE_ID=a.id where 1=1 and d.TARGET_ID=#{userId}
and (a.RESOURE_Type='url' or a.RESOURE_Type='module')
AND e.ID IS NOT NULL AND e.VALIDATE_STATE='1'
