mapper.select:
    	 		select 
						t1.id                             ,
						t1.role_name                      ,
						t1.role_code                      ,
						t1.role_type                      ,
						t1.app_id                         ,
						t1.validate_state                 ,
						t1.version                        

				from sys_role 	t1
				where 1=1

						and t1.ID =#{dto.id}


						and t1.ROLE_NAME =#{dto.roleName}


						and t1.ROLE_CODE =#{dto.roleCode}


						and t1.ROLE_TYPE =#{dto.roleType}


						and t1.APP_ID =#{dto.appId}


						and t1.VALIDATE_STATE =#{dto.validateState}


						and t1.VERSION =#{dto.version}


				order by t1.id desc

    	     select 
						t1.id                             ,
						t1.role_name                      ,
						t1.role_code                      ,
						t1.role_type                      ,
						t1.app_id                         ,
						t1.validate_state                 ,
						t1.version                        
				from sys_role 		t1
				where 1=1

						and t1.ID = #{dto.id}


						and t1.ROLE_NAME = #{dto.roleName}


						and t1.ROLE_CODE = #{dto.roleCode}


						and t1.ROLE_TYPE = #{dto.roleType}


						and t1.APP_ID = #{dto.appId}


						and t1.VALIDATE_STATE = #{dto.validateState}


						and t1.VERSION = #{dto.version}

mapper.insert:
insert into sys_role
(   		
			role_name                      ,
			role_code                      ,
			role_type                      ,
			app_id                         ,
			validate_state                 ,
			version                        
)
values(
			#{dto.roleName,jdbcType=VARCHAR}
			,
			#{dto.roleCode,jdbcType=VARCHAR}
			,
			#{dto.roleType,jdbcType=VARCHAR}
			,
			#{dto.appId,jdbcType=DECIMAL}
			,
			'1'
			,
			#{dto.version,jdbcType=DECIMAL}

)
