mapper.select:
    	 		select 
						t1.id                             ,
						t1.name                           ,
						t1.city                           ,
						t1.createtime                     

				from tcc_teacher 	t1
				where 1=1

						and t1.id =#{dto.id}


						and t1.name =#{dto.name}


						and t1.city =#{dto.city}


						and t1.createtime =#{dto.createtime}


				order by t1.id desc

    	     select 
						t1.id                             ,
						t1.name                           ,
						t1.city                           ,
						t1.createtime                     
				from tcc_teacher 		t1
				where 1=1

						and t1.id = #{dto.id}


						and t1.name = #{dto.name}


						and t1.city = #{dto.city}


						and t1.createtime = #{dto.createtime}

mapper.insert:
insert into tcc_teacher
(   		
			id                             ,
			name                           ,
			city                           ,
			createtime                     
)
values(
			#{dto.id,jdbcType=DECIMAL}
			,
			#{dto.name,jdbcType=VARCHAR}
			,
			#{dto.city,jdbcType=VARCHAR}
			,
			#{dto.createtime,jdbcType=DATE}

)

mapper.update:
update tcc_teacher t1 set
t1.id =#{dto.id,jdbcType=DECIMAL},
t1.name =#{dto.name,jdbcType=VARCHAR},
t1.city =#{dto.city,jdbcType=VARCHAR},
t1.createtime =#{dto.createtime,jdbcType=TIMESTAMP}
where t1.id = #{dto.id}

mapper.select:
select
t1.id                             ,
t1.name                           ,
t1.city                           ,
t1.createtime                     
		from tcc_teacher 		t1
		where 1=1

and t1.id = #{dto.id}


